     1→import {
     2→  createEffect,
     3→  createMemo,
     4→  createResource,
     5→  createSignal,
     6→  For,
     7→  onCleanup,
     8→  Show,
     9→} from "solid-js"
    10→import { useNavigate } from "@tanstack/solid-router"
    11→import { apiClient } from "../api/client"
    12→import type { Event, SessionState } from "../types/api"
    13→import { mcpDispatch } from "../mcp/dispatch"
    14→import { mcpRegistry } from "../mcp/registry"
    15→import { startEventStream } from "../utils/eventStream"
    16→import { dockSessionId, setDockSessionId } from "../state/agentDock"
    17→import "./AgentDock.css"
    18→
    19→interface DockState {
    20→  type: "empty" | "loading" | "error" | "live"
    21→  message?: string
    22→}
    23→
    24→interface TranscriptMessage {
    25→  id: string
    26→  type: "agent" | "user" | "system" | "error"
    27→  timestamp: string
    28→  content: string
    29→}
    30→
    31→interface AgentDockProps {
    32→  sessionId?: string
    33→  onNavigate?: (path: string) => void
    34→}
    35→
    36→export default function AgentDock(props: AgentDockProps) {
    37→  const navigate = useNavigate()
    38→  const sessionId = () => props.sessionId ?? dockSessionId() ?? ""
    39→  const [session] = createResource(
    40→    () => sessionId(),
    41→    (id) => (id ? apiClient.getSession(id) : Promise.resolve(null)),
    42→  )
    43→  const [permissions] = createResource(apiClient.getPermissions)
    44→  const [messages, setMessages] = createSignal<TranscriptMessage[]>([])
    45→  const [dockState, setDockState] = createSignal<DockState>({ type: "empty" })
    46→  const [autoScroll, setAutoScroll] = createSignal(true)
    47→  const [inputValue, setInputValue] = createSignal("")
    48→  const [pendingAction, setPendingAction] = createSignal<string | null>(null)
    49→  const [actionError, setActionError] = createSignal<string | null>(null)
    50→  const [isExpanded, setIsExpanded] = createSignal(false)
    51→  const [rehydrationState, setRehydrationState] = createSignal<
    52→    "idle" | "loading" | "done"
    53→  >("idle")
    54→  const [streamStatus, setStreamStatus] = createSignal<
    55→    "idle" | "connecting" | "reconnecting" | "live" | "disconnected" | "error"
    56→  >("idle")
    57→  const [lastAction, setLastAction] = createSignal<
    58→    { label: string; detail?: string; tone?: "info" | "error" }
    59→    | null
    60→  >(null)
    61→  let transcriptRef: HTMLDivElement | undefined
    62→  let inputRef: HTMLTextAreaElement | undefined
    63→  let dockBootstrap: Promise<string | null> | null = null
    64→
    65→  const canManage = () => permissions()?.can_initiate_bulk_actions ?? false
    66→
    67→  const hasSession = () => Boolean(sessionId())
    68→
    69→  const toggleExpanded = () => setIsExpanded((prev) => !prev)
    70→
    71→  const statusLabel = createMemo(() => {
    72→    if (!hasSession()) return "Idle"
    73→    if (dockState().type === "loading") return "Connecting"
    74→    if (dockState().type === "error") return "Error"
    75→    if (streamStatus() === "connecting") return "Connecting"
    76→    if (streamStatus() === "disconnected") return "Disconnected"
    77→    if (streamStatus() === "reconnecting") return "Reconnecting"
    78→    const state = session()?.state
    79→    if (!state) return "Unknown"
    80→    return state.replace("_", " ")
    81→  })
    82→
    83→  const lastActionLabel = createMemo(() => {
    84→    if (!hasSession()) return "No session"
    85→    return lastAction()?.label ?? "Awaiting activity"
    86→  })
    87→
    88→  const lastActionDetail = createMemo(() => lastAction()?.detail ?? "")
    89→
    90→  const hydrateDockSession = async () => {
    91→    const stored = dockSessionId()
    92→    try {
    93→      const list = await apiClient.listSessions()
    94→      const dock = list.sessions.find(
    95→        (entry) =>
    96→          entry.session_kind === "dock" &&
    97→          ["running", "starting", "paused"].includes(entry.state),
    98→      )
    99→      if (dock) {
   100→        try {
   101→          await apiClient.getSession(dock.id)
   102→          if (dock.id !== stored) {
   103→            setDockSessionId(dock.id)
   104→          }
   105→          return
   106→        } catch {
   107→          // continue to clear stored session
   108→        }
   109→      }
   110→      if (stored) {
   111→        try {
   112→          await apiClient.getSession(stored)
   113→        } catch {
   114→          setDockSessionId(null)
   115→        }
   116→      }
   117→    } catch {
   118→      // Ignore rehydration failures; fallback handled on send.
   119→    }
   120→  }
   121→
   122→  const ensureDockSessionId = async (): Promise<string | null> => {
   123→    const existing = sessionId()
   124→    if (existing) return existing
   125→    if (dockBootstrap) return dockBootstrap
   126→    dockBootstrap = (async () => {
   127→      const stored = dockSessionId()
   128→      if (stored) {
   129→        try {
   130→          await apiClient.getSession(stored)
   131→          return stored
   132→        } catch {
   133→          setDockSessionId(null)
   134→        }
   135→      }
   136→      try {
   137→        const list = await apiClient.listSessions()
   138→        const dock = list.sessions.find(
   139→          (entry) =>
   140→            entry.session_kind === "dock" &&
   141→            ["running", "starting", "paused"].includes(entry.state),
   142→        )
   143→        if (dock) {
   144→          try {
   145→            await apiClient.getSession(dock.id)
   146→            setDockSessionId(dock.id)
   147→            return dock.id
   148→          } catch {
   149→            // ignore stale dock session
   150→          }
   151→        }
   152→      } catch {
   153→        // Continue to create a new dock session.
   154→      }
   155→      try {
   156→        const created = await apiClient.createDockSession()
   157→        setDockSessionId(created.id)
   158→        return created.id
   159→      } catch {
   160→        return null
   161→      }
   162→    })()
   163→    const result = await dockBootstrap
   164→    dockBootstrap = null
   165→    return result
   166→  }
   167→
   168→  const isDockSession = () => session()?.session_kind === "dock" || (!props.sessionId && Boolean(dockSessionId()))
   169→
   170→  const handleDockRequest = async (request: { kind: string; target_id?: string; action?: string; payload?: any }) => {
   171→    if (request.kind === "list") {
   172→      const components = mcpRegistry
   173→        .list()
   174→        .map((entry) => ({ id: entry.id, name: entry.name, description: entry.description }))
   175→      return { ok: true, components }
   176→    }
   177→    if (request.kind === "multi_edit") {
   178→      return mcpDispatch.dispatchMultiFieldEdit(request.payload ?? {})
   179→    }
   180→    if (request.kind === "dispatch") {
   181→      return mcpDispatch.dispatchAction(
   182→        request.target_id ?? "",
   183→        request.action as any,
   184→        request.payload,
   185→      )
   186→    }
   187→    return { ok: false, error: "Unknown dock request" }
   188→  }
   189→
   190→  const formatShort = (value: unknown, limit = 120) => {
   191→    if (value === null || value === undefined) return ""
   192→    const raw = typeof value === "string" ? value : JSON.stringify(value)
   193→    if (raw.length <= limit) return raw
   194→    return `${raw.slice(0, limit - 3)}...`
   195→  }
   196→
   197→  const normalizeActivityEntry = (data: any) => {
   198→    if (!data) return null
   199→    if (Array.isArray(data.entries) && data.entries.length > 0) {
   200→      return data.entries[data.entries.length - 1]
   201→    }
   202→    if (data.entry) return data.entry
   203→    if (data.id && data.kind) return data
   204→    return null
   205→  }
   206→
   207→  const formatActivityContent = (entry: any) => {
   208→    const data = entry?.data ?? {}
   209→    if (typeof data.text === "string" && data.text.trim().length > 0) return data.text
   210→    if (typeof data.content === "string" && data.content.trim().length > 0) return data.content
   211→    if (typeof data.message === "string" && data.message.trim().length > 0) return data.message
   212→    if (typeof data.summary === "string" && data.summary.trim().length > 0) return data.summary
   213→    if (typeof data.tool === "string") {
   214→      if (typeof data.result === "string") {
   215→        return `Tool ${data.tool}: ${data.result}`
   216→      }
   217→      return `Tool ${data.tool}`
   218→    }
   219→    return `${entry?.kind ?? "activity"}`
   220→  }
   221→
   222→  const markStreamActive = () => {
   223→    if (streamStatus() !== "live") setStreamStatus("live")
   224→  }
   225→
   226→  // Stream setup
   227→  createEffect(() => {
   228→    const activeSessionId = sessionId()
   229→    if (!activeSessionId) {
   230→      setDockState({ type: "empty" })
   231→      setMessages([])
   232→      setStreamStatus("idle")
   233→      setLastAction(null)
   234→      return
   235→    }
   236→
   237→    if (session.loading) {
   238→      setDockState({ type: "loading" })
   239→      setStreamStatus("connecting")
   240→      return
   241→    }
   242→
   243→    if (session.error) {
   244→      setDockState({
   245→        type: "error",
   246→        message: "Failed to connect to session. Check the full viewer for details.",
   247→      })
   248→      setStreamStatus("error")
   249→      return
   250→    }
   251→
   252→    const sess = session()
   253→    if (!sess) {
   254→      setDockState({ type: "empty" })
   255→      setStreamStatus("idle")
   256→      return
   257→    }
   258→
   259→    // Reset state for new session
   260→    setMessages([])
   261→    setAutoScroll(true)
   262→    setDockState({ type: "live" })
   263→    setStreamStatus("connecting")
   264→    setLastAction(null)
   265→
   266→    // Connect to event stream
   267→    const handleEvent = (eventType: Event["type"], event: MessageEvent) => {
   268→      if (typeof event.data !== "string") return
   269→      let payload: Event | null = null
   270→      try {
   271→        const parsed = JSON.parse(event.data)
   272→        if (parsed && typeof parsed === "object" && "type" in parsed) {
   273→          payload = parsed as Event
   274→        } else {
   275→          payload = {
   276→            type: eventType,
   277→            timestamp: new Date().toISOString(),
   278→            session_id: activeSessionId,
   279→            data: parsed,
   280→          }
   281→        }
   282→      } catch (err) {
   283→        console.error("Failed to parse stream event:", err)
   284→        return
   285→      }
   286→
   287→      if (!payload) return
   288→      markStreamActive()
   289→
   290→      switch (payload.type) {
   291→        case "output": {
   292→          const content = payload.data?.content || ""
   293→          if (content) {
   294→            setMessages((prev) => [
   295→              ...prev,
   296→              {
   297→                id: crypto.randomUUID(),
   298→                type: "agent",
   299→                timestamp: payload.timestamp,
   300→                content,
   301→              },
   302→            ])
   303→            if (autoScroll()) {
   304→              requestAnimationFrame(() => {
   305→                if (transcriptRef) {
   306→                  transcriptRef.scrollTop = transcriptRef.scrollHeight
   307→                }
   308→              })
   309→            }
   310→            setLastAction({ label: "Output", detail: formatShort(content) })
   311→          }
   312→          if (dockState().type === "loading") {
   313→            setDockState({ type: "live" })
   314→          }
   315→          break
   316→        }
   317→        case "status_change": {
   318→          const detail = `${payload.data?.old_state ?? "?"} -> ${payload.data?.new_state ?? "?"}`
   319→          setLastAction({ label: "Status change", detail })
   320→          if (dockState().type === "loading") {
   321→            setDockState({ type: "live" })
   322→          }
   323→          break
   324→        }
   325→        case "metadata": {
   326→          const key = payload.data?.key ?? "metadata"
   327→          const value = formatShort(payload.data?.value)
   328→          setLastAction({ label: `Metadata: ${key}`, detail: value })
   329→          if (dockState().type === "loading") {
   330→            setDockState({ type: "live" })
   331→          }
   332→          break
   333→        }
   334→        case "activity_entry": {
   335→          const entry = normalizeActivityEntry(payload.data)
   336→          if (entry) {
   337→            setLastAction({
   338→              label: `Action: ${entry.kind ?? "activity"}`,
   339→              detail: formatShort(formatActivityContent(entry)),
   340→            })
   341→          }
   342→          if (dockState().type === "loading") {
   343→            setDockState({ type: "live" })
   344→          }
   345→          break
   346→        }
   347→        case "error": {
   348→          const errorMessage = payload.data?.message || "Unknown error"
   349→          setMessages((prev) => [
   350→            ...prev,
   351→            {
   352→              id: crypto.randomUUID(),
   353→              type: "error",
   354→              timestamp: payload.timestamp,
   355→              content: errorMessage,
   356→            },
   357→          ])
   358→          setLastAction({ label: "Error", detail: errorMessage, tone: "error" })
   359→          setDockState({
   360→            type: "error",
   361→            message: "Stream error: " + errorMessage,
   362→          })
   363→          setStreamStatus("error")
   364→          break
   365→        }
   366→        case "metric": {
   367→          const detail = `in ${payload.data?.tokens_in ?? "-"} · out ${payload.data?.tokens_out ?? "-"}`
   368→          setLastAction({ label: "Metrics", detail })
   369→          if (dockState().type === "loading") {
   370→            setDockState({ type: "live" })
   371→          }
   372→          break
   373→        }
   374→        default:
   375→          break
   376→      }
   377→    }
   378→
   379→    const handleHeartbeat = () => {
   380→      markStreamActive()
   381→    }
   382→
   383→    const stream = startEventStream(
   384→      apiClient.getEventsUrl(activeSessionId),
   385→      {
   386→        onStatus: (status) => {
   387→          if (status === "connecting") {
   388→            setStreamStatus("connecting")
   389→          } else if (status === "backoff") {
   390→            setStreamStatus("reconnecting")
   391→            setDockState({
   392→              type: "error",
   393→              message: "Connection lost. Attempting to reconnect...",
   394→            })
   395→          } else if (status === "not_found") {
   396→            setStreamStatus("error")
   397→            setDockState({
   398→              type: "error",
   399→              message: "Stream endpoint not found.",
   400→            })
   401→          }
   402→        },
   403→        onOpen: () => {
   404→          setStreamStatus("live")
   405→          if (dockState().type !== "live") {
   406→            setDockState({ type: "live" })
   407→          }
   408→        },
   409→        onError: (status) => {
   410→          if (status === 404) {
   411→            setStreamStatus("error")
   412→            setDockState({
   413→              type: "error",
   414→              message: "Stream endpoint not found.",
   415→            })
   416→            return
   417→          }
   418→          setStreamStatus("disconnected")
   419→        },
   420→        onEventSource: (source) => {
   421→          const bind = (type: Event["type"]) =>
   422→            source.addEventListener(type, (event) => handleEvent(type, event as MessageEvent))
   423→          bind("output")
   424→          bind("status_change")
   425→          bind("metric")
   426→          bind("error")
   427→          bind("metadata")
   428→          bind("activity_entry")
   429→          source.addEventListener("heartbeat", handleHeartbeat)
   430→        },
   431→      },
   432→      {
   433→        connectionTimeoutMs: 10000,
   434→      },
   435→    )
   436→
   437→    onCleanup(() => {
   438→      stream.close()
   439→    })
   440→  })
   441→
   442→  // Restore dock session on load (dock-only)
   443→  createEffect(() => {
   444→    if (props.sessionId) return
   445→    if (rehydrationState() !== "idle") return
   446→    setRehydrationState("loading")
   447→    void hydrateDockSession().finally(() => setRehydrationState("done"))
   448→  })
   449→
   450→  // Dock MCP polling
   451→  createEffect(() => {
   452→    const activeSessionId = sessionId()
   453→    if (!activeSessionId || !isDockSession()) return
   454→    if (import.meta.env.MODE === "test") return
   455→
   456→    let cancelled = false
   457→    const controller = new AbortController()
   458→
   459→    const run = async () => {
   460→      while (!cancelled) {
   461→        try {
   462→          const req = await apiClient.pollDockMcp(activeSessionId, { timeoutMs: 20000 })
   463→          if (!req) continue
   464→          const result = await handleDockRequest(req)
   465→          await apiClient.respondDockMcp(activeSessionId, {
   466→            id: req.id,
   467→            result,
   468→          })
   469→        } catch (error) {
   470→          if (controller.signal.aborted) return
   471→          await new Promise((resolve) => setTimeout(resolve, 1000))
   472→        }
   473→      }
   474→    }
   475→
   476→    void run()
   477→
   478→    onCleanup(() => {
   479→      cancelled = true
   480→      controller.abort()
   481→    })
   482→  })
   483→
   484→  // Handle manual scroll detection
   485→  const handleTranscriptScroll = () => {
   486→    if (!transcriptRef) return
   487→    const isNearBottom =
   488→      transcriptRef.scrollHeight - transcriptRef.scrollTop -
   489→      transcriptRef.clientHeight <
   490→      100
   491→    setAutoScroll(isNearBottom)
   492→  }
   493→
   494→  // Keyboard shortcuts
   495→  const handleInputKeyDown = (e: KeyboardEvent) => {
   496→    if (e.key === "Enter" && !e.shiftKey) {
   497→      e.preventDefault()
   498→      handleSendMessage()
   499→    }
   500→  }
   501→
   502→  const handleSendMessage = async () => {
   503→    const message = inputValue().trim()
   504→    if (!message) return
   505→
   506→    setPendingAction("send")
   507→    setActionError(null)
   508→
   509→    try {
   510→      let activeSessionId = sessionId()
   511→      if (!activeSessionId) {
   512→        setDockState({ type: "loading" })
   513→        setStreamStatus("connecting")
   514→        activeSessionId = await ensureDockSessionId()
   515→      }
   516→      if (!activeSessionId) {
   517→        throw new Error("Unable to start dock session.")
   518→      }
   519→      const providerType = session()?.provider_type
   520→      const payload = providerType === "pty" && !message.endsWith("\n")
   521→        ? `${message}\n`
   522→        : message
   523→      await apiClient.sendSessionInput(activeSessionId, payload)
   524→      setMessages((prev) => [
   525→        ...prev,
   526→        {
   527→          id: crypto.randomUUID(),
   528→          type: "user",
   529→          timestamp: new Date().toISOString(),
   530→          content: message,
   531→        },
   532→      ])
   533→      setLastAction({ label: "Input", detail: formatShort(message) })
   534→      setInputValue("")
   535→    } catch (error) {
   536→      setActionError(
   537→        error instanceof Error ? error.message : "Failed to send input",
   538→      )
   539→    } finally {
   540→      setPendingAction(null)
   541→    }
   542→  }
   543→
   544→  const handlePauseResume = async () => {
   545→    if (!sessionId() || !session()) return
   546→
   547→    const state = session()?.state
   548→    const action = state === "paused" ? "resume" : "pause"
   549→
   550→    setPendingAction(action)
   551→    setActionError(null)
   552→
   553→    try {
   554→      if (action === "pause") {
   555→        await apiClient.pauseSession(sessionId())
   556→      } else {
   557→        await apiClient.resumeSession(sessionId())
   558→      }
   559→    } catch (error) {
   560→      setActionError(
   561→        error instanceof Error ? error.message : "Action failed",
   562→      )
   563→    } finally {
   564→      setPendingAction(null)
   565→    }
   566→  }
   567→
   568→  const handleKillSession = async () => {
   569→    if (!sessionId()) return
   570→
   571→    if (!window.confirm("Terminate this session? This cannot be undone.")) {
   572→      return
   573→    }
   574→
   575→    setPendingAction("stop")
   576→    setActionError(null)
   577→
   578→    try {
   579→      await apiClient.stopSession(sessionId())
   580→    } catch (error) {
   581→      setActionError(
   582→        error instanceof Error ? error.message : "Failed to stop session",
   583→      )
   584→    } finally {
   585→      setPendingAction(null)
   586→    }
   587→  }
   588→
   589→  const handleOpenFullViewer = () => {
   590→    const id = sessionId()
   591→    if (!id) return
   592→    if (props.onNavigate) {
   593→      props.onNavigate(`/sessions/${id}`)
   594→      return
   595→    }
   596→    navigate({ to: `/sessions/${id}` })
   597→  }
   598→
   599→  const sessionState = () => session()?.state as SessionState | undefined
   600→  const isSessionActive = () =>
   601→    sessionState() && ["running", "paused"].includes(sessionState() || "")
   602→
   603→  return (
   604→    <div
   605→      class="agent-dock"
   606→      data-testid="agent-dock"
   607→      classList={{ minimized: !isExpanded(), active: hasSession(), idle: !hasSession() }}
   608→    >
   609→      <div class="agent-dock-header">
   610→        <div>
   611→          <p class="agent-dock-title">Agent Box</p>
   612→          <p class="agent-dock-subtitle">MCP activity feed</p>
   613→        </div>
   614→        <div class="agent-dock-summary">
   615→          <span class={`agent-dock-status ${dockState().type}`}>{statusLabel()}</span>
   616→          <span class="agent-dock-last-action" title={lastActionDetail()}>
   617→            {lastActionLabel()}
   618→          </span>
   619→        </div>
   620→        <button
   621→          type="button"
   622→          class="agent-dock-toggle"
