     1→import type {
     2→  McpActionKey,
     3→  McpActionPayloads,
     4→  McpActionResult,
     5→  McpMultiFieldEditPayload,
     6→  McpMultiFieldEditResult,
     7→} from "./contract";
     8→import type { McpRegistry, McpRegistryEntry } from "./registry";
     9→import { mcpRegistry } from "./registry";
    10→
    11→const pulseClassName = "mcp-pulse";
    12→const pulseDurationMs = 360;
    13→
    14→const normalizeEditPayload = (payload: McpMultiFieldEditPayload) => {
    15→  if (Array.isArray(payload)) {
    16→    return payload.reduce<Record<string, unknown>>((acc, entry) => {
    17→      acc[entry.fieldId] = entry.value;
    18→      return acc;
    19→    }, {});
    20→  }
    21→  return payload;
    22→};
    23→
    24→const applyPulse = (element: HTMLElement) => {
    25→  element.classList.remove(pulseClassName);
    26→  void element.offsetWidth;
    27→  element.classList.add(pulseClassName);
    28→  window.setTimeout(() => element.classList.remove(pulseClassName), pulseDurationMs);
    29→};
    30→
    31→const runActionAnimation = async (element?: HTMLElement | null) => {
    32→  if (!element) return;
    33→  if (typeof element.scrollIntoView === "function") {
    34→    element.scrollIntoView({ behavior: "smooth", block: "center", inline: "center" });
    35→  }
    36→  applyPulse(element);
    37→  await new Promise<void>((resolve) => {
    38→    requestAnimationFrame(() => resolve());
    39→  });
    40→};
    41→
    42→const buildContext = (entry: McpRegistryEntry) => ({
    43→  id: entry.id,
    44→  name: entry.name,
    45→  description: entry.description,
    46→  getValue: entry.getValue,
    47→});
    48→
    49→export const createMcpDispatch = (registry: McpRegistry) => {
    50→  const dispatchAction = async <K extends McpActionKey>(
    51→    id: string,
    52→    action: K,
    53→    payload: K extends keyof McpActionPayloads ? McpActionPayloads[K] : unknown,
    54→  ): Promise<McpActionResult> => {
    55→    const entry = registry.get(id);
    56→    if (!entry) {
    57→      return { ok: false, error: "Unknown MCP component" };
    58→    }
    59→
    60→    const handler = entry.actions[action];
    61→    if (!handler) {
    62→      return { ok: false, error: `Action '${action}' not supported` };
    63→    }
    64→
    65→    await runActionAnimation(entry.element);
    66→
    67→    try {
    68→      const result = await handler(payload as never, buildContext(entry));
    69→      if (!result || typeof result.ok !== "boolean") {
    70→        return { ok: false, error: "Action handler returned invalid result" };
    71→      }
    72→      return result;
    73→    } catch (error) {
    74→      const message = error instanceof Error ? error.message : "Action handler failed";
    75→      return { ok: false, error: message };
    76→    }
    77→  };
    78→
    79→  const dispatchMultiFieldEdit = async (
    80→    payload: McpMultiFieldEditPayload,
    81→  ): Promise<McpMultiFieldEditResult> => {
    82→    const normalized = normalizeEditPayload(payload);
    83→    const results: Record<string, { ok: boolean; error?: string }> = {};
    84→    let ok = true;
    85→
    86→    for (const [fieldId, value] of Object.entries(normalized)) {
    87→      const result = await dispatchAction(fieldId, "edit", {
    88→        value: value === undefined || value === null ? "" : String(value),
    89→      });
    90→      results[fieldId] = { ok: result.ok, error: result.error };
    91→      if (!result.ok) ok = false;
    92→    }
    93→
    94→    return { ok, results };
    95→  };
    96→
    97→  return { dispatchAction, dispatchMultiFieldEdit };
    98→};
    99→
   100→export const mcpDispatch = createMcpDispatch(mcpRegistry);
   101→

