     1→import { createFileRoute, useNavigate } from '@tanstack/solid-router'
     2→import { createResource, createSignal, createEffect, createMemo, onCleanup, Show, For } from 'solid-js'
     3→import { apiClient } from '../../api/client'
     4→import TerminalView from '../../components/TerminalView'
     5→import type { ActivityEntry, ActivityEntryMutation, Event, SessionState } from '../../types/api'
     6→import { dockSessionId, setDockSessionId } from '../../state/agentDock'
     7→import { startEventStream } from '../../utils/eventStream'
     8→
     9→export const Route = createFileRoute('/sessions/$sessionId')({
    10→  component: SessionViewer,
    11→})
    12→
    13→interface TranscriptMessage {
    14→  id: string
    15→  type: "agent" | "user" | "system" | "error"
    16→  timestamp: string
    17→  content: string
    18→  entryId?: string
    19→  revision?: number
    20→  open?: boolean
    21→  kind?: string
    22→}
    23→
    24→interface SessionViewerProps {
    25→  sessionId?: string
    26→  onNavigate?: (path: string) => void
    27→  onDockSession?: (id: string) => void
    28→  onClose?: () => void
    29→}
    30→
    31→const CODE_BLOCK_REGEX = /```(\w+)?\n([\s\S]*?)```/g
    32→
    33→export default function SessionViewer(props: SessionViewerProps = {}) {
    34→  const navigate = useNavigate()
    35→  const routeParams = props.sessionId ? null : Route.useParams()
    36→  const sessionId = () => props.sessionId ?? routeParams?.().sessionId ?? ""
    37→
    38→  const [session, { refetch: refetchSession }] = createResource(sessionId, apiClient.getSession)
    39→  const [permissions] = createResource(apiClient.getPermissions)
    40→  const [messages, setMessages] = createSignal<TranscriptMessage[]>([])
    41→  const [activityCursor, setActivityCursor] = createSignal<string | null>(null)
    42→  const [activityHistoryReady, setActivityHistoryReady] = createSignal(false)
    43→  const [activityHistoryLoading, setActivityHistoryLoading] = createSignal(false)
    44→  const [hasActivityHistory, setHasActivityHistory] = createSignal(false)
    45→  const [initialOutput, setInitialOutput] = createSignal<string | null>(null)
    46→  const [initialOutputTimestamp, setInitialOutputTimestamp] = createSignal<string | null>(null)
    47→  const [initialOutputApplied, setInitialOutputApplied] = createSignal(false)
    48→  const [filter, setFilter] = createSignal("")
    49→  const [autoScroll, setAutoScroll] = createSignal(true)
    50→  const [streamStatus, setStreamStatus] = createSignal("connecting")
    51→  const [terminalStatus, setTerminalStatus] = createSignal<
    52→    "connecting" | "live" | "closed" | "error" | "resyncing"
    53→  >("connecting")
    54→  const [initialized, setInitialized] = createSignal(false)
    55→  const [lastHeartbeatAt, setLastHeartbeatAt] = createSignal<number | null>(null)
    56→  const [actionNotice, setActionNotice] = createSignal<{ tone: "error" | "success"; message: string } | null>(
    57→    null,
    58→  )
    59→  const [pendingAction, setPendingAction] = createSignal<"pause" | "resume" | "stop" | null>(null)
    60→  let transcriptRef: HTMLDivElement | undefined
    61→
    62→  const [sessionStateOverride, setSessionStateOverride] = createSignal<SessionState | null>(null)
    63→  const sessionState = () => sessionStateOverride() ?? session()?.state ?? "created"
    64→  const providerType = () => session()?.provider_type ?? ""
    65→  const canInspect = () => permissions()?.can_inspect_sessions ?? false
    66→  const canManage = () => permissions()?.can_initiate_bulk_actions ?? false
    67→
    68→  const filteredMessages = createMemo(() => {
    69→    const term = filter().trim().toLowerCase()
    70→    if (!term) return messages()
    71→    return messages().filter((msg) =>
    72→      msg.content.toLowerCase().includes(term) || msg.type.toLowerCase().includes(term),
    73→    )
    74→  })
    75→
    76→  const mergeMessages = (incoming: TranscriptMessage[], options: { sort?: boolean } = {}) => {
    77→    if (incoming.length === 0) return
    78→    setMessages((prev) => {
    79→      const merged = [...prev]
    80→      const indexById = new Map<string, number>()
    81→      merged.forEach((msg, idx) => indexById.set(msg.id, idx))
    82→      for (const next of incoming) {
    83→        const existingIndex = indexById.get(next.id)
    84→        if (existingIndex !== undefined) {
    85→          const existing = merged[existingIndex]
    86→          if (
    87→            next.revision !== undefined &&
    88→            existing.revision !== undefined &&
    89→            next.revision < existing.revision
    90→          ) {
    91→            continue
    92→          }
    93→          merged[existingIndex] = { ...existing, ...next }
    94→        } else {
    95→          merged.push(next)
    96→          indexById.set(next.id, merged.length - 1)
    97→        }
    98→      }
    99→      if (options.sort) {
   100→        merged.sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
   101→      }
   102→      return merged
   103→    })
   104→  }
   105→
   106→  const removeMessageById = (id: string) => {
   107→    setMessages((prev) => prev.filter((msg) => msg.id !== id))
   108→  }
   109→
   110→  const pushMessage = (message: TranscriptMessage) => {
   111→    setMessages((prev) => [...prev, message])
   112→  }
   113→
   114→  const markStreamActive = () => {
   115→    setLastHeartbeatAt(Date.now())
   116→    if (streamStatus() !== "live") {
   117→      setStreamStatus("live")
   118→    }
   119→  }
   120→
   121→  const handleEvent = (eventType: string, event: MessageEvent) => {
   122→    markStreamActive()
   123→    if (typeof event.data !== "string") return
   124→    let payload: Event | null = null
   125→    try {
   126→      const parsed = JSON.parse(event.data)
   127→      if (parsed && typeof parsed === "object" && "type" in parsed) {
   128→        payload = parsed
   129→      } else {
   130→        payload = {
   131→          type: eventType as Event["type"],
   132→          timestamp: new Date().toISOString(),
   133→          session_id: sessionId(),
   134→          data: parsed,
   135→        }
   136→      }
   137→    } catch (err) {
   138→      pushMessage({
   139→        id: crypto.randomUUID(),
   140→        type: "error",
   141→        timestamp: new Date().toISOString(),
   142→        content: "Failed to parse stream event payload.",
   143→      })
   144→      return
   145→    }
   146→
   147→    if (!payload) return
   148→
   149→    switch (payload.type) {
   150→      case "output": {
   151→        if (providerType() === "pty") {
   152→          break
   153→        }
   154→        const content = payload.data?.content ?? ""
   155→        if (content) {
   156→          pushMessage({
   157→            id: crypto.randomUUID(),
   158→            type: "agent",
   159→            timestamp: payload.timestamp,
   160→            content,
   161→          })
   162→        }
   163→        break
   164→      }
   165→      case "status_change": {
   166→        const content = `State changed: ${payload.data?.old_state} -> ${payload.data?.new_state}`
   167→        if (payload.data?.new_state) {
   168→          setSessionStateOverride(payload.data.new_state as SessionState)
   169→        }
   170→        void refetchSession()
   171→        pushMessage({
   172→          id: crypto.randomUUID(),
   173→          type: "system",
   174→          timestamp: payload.timestamp,
   175→          content,
   176→        })
   177→        break
   178→      }
   179→      case "metric": {
   180→        const content = `Metrics updated - in ${payload.data?.tokens_in} - out ${payload.data?.tokens_out} - requests ${payload.data?.request_count}`
   181→        pushMessage({
   182→          id: crypto.randomUUID(),
   183→          type: "system",
   184→          timestamp: payload.timestamp,
   185→          content,
   186→        })
   187→        break
   188→      }
   189→      case "error": {
   190→        const content = payload.data?.message ?? "Unknown error"
   191→        pushMessage({
   192→          id: crypto.randomUUID(),
   193→          type: "error",
   194→          timestamp: payload.timestamp,
   195→          content,
   196→        })
   197→        break
   198→      }
   199→      case "metadata": {
   200→        const key = payload.data?.key ?? "metadata"
   201→        const value = payload.data?.value
   202→        const content = `Metadata - ${key}: ${typeof value === "string" ? value : JSON.stringify(value)}`
   203→        pushMessage({
   204→          id: crypto.randomUUID(),
   205→          type: "system",
   206→          timestamp: payload.timestamp,
   207→          content,
   208→        })
   209→
   210→        break
   211→      }
   212→      case "activity_entry": {
   213→        const mutation = normalizeActivityMutation(payload.data)
   214→        if (mutation.entries && mutation.entries.length > 0) {
   215→          const messages = mutation.entries.map((entry) => toActivityMessage(entry))
   216→          mergeMessages(messages, { sort: true })
   217→          break
   218→        }
   219→        if (!mutation.entry) break
   220→        const entryId = toActivityMessageId(mutation.entry)
   221→        if (mutation.action === "delete") {
   222→          removeMessageById(entryId)
   223→          break
   224→        }
   225→        mergeMessages([toActivityMessage(mutation.entry)], { sort: true })
   226→        break
   227→      }
   228→      default:
   229→        break
   230→    }
   231→  }
   232→
   233→  const handleHeartbeat = () => {
   234→    markStreamActive()
   235→  }
   236→
   237→  const loadActivityHistory = async (options: { cursor?: string | null; reset?: boolean } = {}) => {
   238→    if (activityHistoryLoading()) return
   239→    const id = sessionId()
   240→    if (!id) return
   241→    setActivityHistoryLoading(true)
   242→    if (options.reset) {
   243→      setActivityCursor(null)
   244→      setHasActivityHistory(false)
   245→    }
   246→    try {
   247→      const response = await apiClient.getActivityEntries(id, {
   248→        limit: 100,
   249→        cursor: options.cursor ?? undefined,
   250→      })
   251→      const entries = response?.entries ?? []
   252→      if (entries.length > 0) {
   253→        setHasActivityHistory(true)
   254→        mergeMessages(entries.map((entry) => toActivityMessage(entry)), { sort: true })
   255→      }
   256→      setActivityCursor(response?.next_cursor ?? null)
   257→    } catch (error) {
   258→      // ignore load failures; stream will still populate if available
   259→    } finally {
   260→      setActivityHistoryLoading(false)
   261→      setActivityHistoryReady(true)
   262→    }
   263→  }
   264→
   265→  const handleLoadEarlier = () => {
   266→    if (!activityCursor()) return
   267→    void loadActivityHistory({ cursor: activityCursor() })
   268→  }
   269→
   270→  const scrollToBottom = () => {
   271→    if (!transcriptRef) return
   272→    transcriptRef.scrollTop = transcriptRef.scrollHeight
   273→  }
   274→
   275→  createEffect(() => {
   276→    if (initialized() || session.loading) return
   277→    const initial = session()
   278→    if (!initial) return
   279→    setInitialized(true)
   280→    setSessionStateOverride(initial.state)
   281→    pushMessage({
   282→      id: crypto.randomUUID(),
   283→      type: "system",
   284→      timestamp: initial.updated_at,
   285→      content: `Session ${initial.id} - ${initial.provider_type} - ${initial.state}`,
   286→    })
   287→    if (initial.output) {
   288→      setInitialOutput(initial.output)
   289→      setInitialOutputTimestamp(initial.updated_at)
   290→    }
   291→  })
   292→
   293→  createEffect(() => {
   294→    if (initialOutputApplied()) return
   295→    const initial = session()
   296→    if (!initial) return
   297→    if (!activityHistoryReady()) return
   298→    if (initial.provider_type === "pty") {
   299→      setInitialOutputApplied(true)
   300→      return
   301→    }
   302→    if (!initialOutput() || hasActivityHistory()) {
   303→      setInitialOutputApplied(true)
   304→      return
   305→    }
   306→    pushMessage({
   307→      id: crypto.randomUUID(),
   308→      type: "agent",
   309→      timestamp: initialOutputTimestamp() ?? initial.updated_at,
   310→      content: initialOutput() ?? "",
   311→    })
   312→    setInitialOutputApplied(true)
   313→  })
   314→
   315→  createEffect(() => {
   316→    const data = session()
   317→    if (!data) return
   318→    setSessionStateOverride(data.state)
   319→  })
   320→
   321→
   322→  createEffect(() => {
   323→    const id = sessionId()
   324→    if (!id || permissions.loading) return
   325→    if (!canInspect()) {
   326→      setActivityHistoryReady(true)
   327→      return
   328→    }
   329→    setActivityHistoryReady(false)
   330→    setInitialOutputApplied(false)
   331→    void loadActivityHistory({ reset: true })
   332→  })
   333→
   334→  createEffect(() => {
   335→    if (permissions.loading) return
   336→    if (!canInspect()) return
   337→    const url = apiClient.getEventsUrl(sessionId())
   338→    const isTestEnv =
   339→      (typeof import.meta !== "undefined" && import.meta.env?.MODE === "test") ||
   340→      (typeof process !== "undefined" && Boolean(process.env?.VITEST))
   341→    const connectionTimeoutMs = 10000
   342→
   343→    const heartbeatTimeoutMs = 35000
   344→    const heartbeatCheckIntervalMs = 5000
   345→    const heartbeatInterval = window.setInterval(() => {
   346→      const lastHeartbeat = lastHeartbeatAt()
   347→      if (!lastHeartbeat) return
   348→      if (Date.now() - lastHeartbeat > heartbeatTimeoutMs) {
   349→        setStreamStatus("disconnected")
   350→      }
   351→    }, heartbeatCheckIntervalMs)
   352→
   353→    const stream = startEventStream(
   354→      url,
   355→      {
   356→        onStatus: (status) => {
   357→          if (status === "connecting") {
   358→            setStreamStatus("connecting")
   359→          } else if (status === "backoff") {
   360→            setStreamStatus("reconnecting")
   361→          } else if (status === "not_found") {
   362→            setStreamStatus("connection_failed")
   363→          }
   364→        },
   365→        onOpen: () => {
   366→          markStreamActive()
   367→        },
   368→        onTimeout: () => {
   369→          setStreamStatus("connection_timeout")
   370→        },
   371→        onError: (status) => {
   372→          if (status === 404) {
   373→            setStreamStatus("connection_failed")
   374→            return
   375→          }
   376→          if (streamStatus() === "connection_timeout") return
   377→          if (streamStatus() === "connecting") {
   378→            setStreamStatus("connection_failed")
   379→          } else {
   380→            setStreamStatus("disconnected")
   381→          }
   382→        },
   383→        onEventSource: (source) => {
   384→          const bind = (type: string) =>
   385→            source.addEventListener(type, (event) => handleEvent(type, event as MessageEvent))
   386→          bind("output")
   387→          bind("status_change")
   388→          bind("metric")
   389→          bind("error")
   390→          bind("metadata")
   391→          bind("activity_entry")
   392→          source.addEventListener("heartbeat", handleHeartbeat)
   393→        },
   394→      },
   395→      {
   396→        connectionTimeoutMs,
   397→        preflight: !isTestEnv,
   398→      },
   399→    )
   400→
   401→    onCleanup(() => {
   402→      stream.close()
   403→      window.clearInterval(heartbeatInterval)
   404→    })
   405→  })
   406→
   407→  createEffect(() => {
   408→    messages()
   409→    if (!autoScroll()) return
   410→    scrollToBottom()
   411→  })
   412→
   413→  const handleScroll = () => {
   414→    if (!transcriptRef) return
   415→    const buffer = 80
   416→    const distance = transcriptRef.scrollHeight - transcriptRef.scrollTop - transcriptRef.clientHeight
   417→    if (distance <= buffer) {
   418→      setAutoScroll(true)
   419→    } else if (autoScroll()) {
   420→      setAutoScroll(false)
   421→    }
   422→  }
   423→
   424→  const handlePause = async () => {
   425→    if (!canManage()) {
   426→      setActionNotice({
   427→        tone: "error",
   428→        message: "Bulk session controls are not permitted for your role.",
   429→      })
   430→      return
   431→    }
   432→    setPendingAction("pause")
   433→    setActionNotice(null)
   434→    try {
   435→      await apiClient.pauseSession(sessionId())
   436→      setActionNotice({ tone: "success", message: "Pause request sent." })
   437→    } catch (error) {
   438→      setActionNotice({ tone: "error", message: formatActionError(error) })
   439→    } finally {
   440→      setPendingAction(null)
   441→    }
   442→  }
   443→
   444→  const handleResume = async () => {
   445→    if (!canManage()) {
   446→      setActionNotice({
   447→        tone: "error",
   448→        message: "Bulk session controls are not permitted for your role.",
   449→      })
   450→      return
   451→    }
   452→    setPendingAction("resume")
   453→    setActionNotice(null)
   454→    try {
   455→      await apiClient.resumeSession(sessionId())
   456→      setActionNotice({ tone: "success", message: "Resume request sent." })
   457→    } catch (error) {
   458→      setActionNotice({ tone: "error", message: formatActionError(error) })
   459→    } finally {
   460→      setPendingAction(null)
   461→    }
   462→  }
   463→
   464→  const handleStop = async () => {
   465→    if (!canManage()) {
   466→      setActionNotice({
   467→        tone: "error",
   468→        message: "Bulk session controls are not permitted for your role.",
   469→      })
   470→      return
   471→    }
   472→    if (!window.confirm("Kill this session immediately?")) return
   473→    setPendingAction("stop")
   474→    setActionNotice(null)
   475→    try {
   476→      await apiClient.stopSession(sessionId())
   477→      setActionNotice({ tone: "success", message: "Kill request sent." })
   478→    } catch (error) {
   479→      setActionNotice({ tone: "error", message: formatActionError(error) })
   480→    } finally {
   481→      setPendingAction(null)
   482→    }
   483→  }
   484→
   485→  const stateLabel = (state: SessionState) => state.replace("_", " ")
   486→
   487→  const exportTranscript = (format: "json" | "markdown") => {
   488→    const data = messages()
   489→    if (format === "json") {
   490→      downloadFile(`${sessionId()}-transcript.json`, JSON.stringify(data, null, 2))
   491→      return
   492→    }
   493→    const markdown = data
   494→      .map((msg) => `### ${msg.type.toUpperCase()} · ${msg.timestamp}\n\n${msg.content}\n`)
   495→      .join("\n")
   496→    downloadFile(`${sessionId()}-transcript.md`, markdown)
   497→  }
   498→
   499→  createEffect(() => {
   500→    const id = sessionId()
   501→    if (!id) return
   502→    if (!dockSessionId()) {
   503→      setDockSessionId(id)
   504→    }
   505→    if (props.onDockSession) props.onDockSession(id)
   506→  })
   507→
   508→  const handleClose = () => {
   509→    if (props.onClose) {
   510→      props.onClose()
   511→      return
   512→    }
   513→    if (props.onNavigate) {
   514→      props.onNavigate("/sessions")
   515→      return
   516→    }
   517→    navigate({ to: "/sessions" })
   518→  }
   519→
   520→  return (
   521→    <div class="session-viewer">
   522→      <header class="view-header">
   523→        <div>
   524→          <p class="eyebrow">Session Viewer</p>
   525→          <h1 data-testid="session-viewer-heading">Live Session Control</h1>
   526→          <p class="dashboard-subtitle">Track the real-time transcript, monitor PTY output, and intervene fast.</p>
   527→        </div>
   528→        <div class="session-meta">
   529→          <div class="stream-pill-group">
   530→            <span class={`state-badge ${sessionState()}`} data-testid="session-state-badge">
   531→              {stateLabel(sessionState())}
   532→            </span>
   533→            <span class={`stream-pill ${streamStatus()}`} data-testid="activity-stream-status">
   534→              Activity {getStreamStatusLabel(streamStatus())}
   535→            </span>
   536→            <Show when={providerType() === "pty"}>
   537→              <span class={`stream-pill ${terminalStatus()}`} data-testid="terminal-stream-status">
   538→                Terminal {getTerminalStatusLabel(terminalStatus())}
   539→              </span>
   540→            </Show>
   541→          </div>
   542→          <div class="session-actions">
   543→            <button type="button" class="neutral" onClick={() => exportTranscript("json")}>
   544→              Export JSON
   545→            </button>
   546→            <button type="button" class="neutral" onClick={() => exportTranscript("markdown")}>
   547→              Export Markdown
   548→            </button>
   549→            <button
   550→              type="button"
   551→              onClick={handlePause}
   552→              disabled={!canManage() || sessionState() !== "running" || pendingAction() === "pause"}
   553→              title={
   554→                !canManage()
   555→                  ? "Bulk session controls are not permitted for your role."
   556→                  : pendingAction() === "pause"
   557→                  ? "Pause action is in progress..."
   558→                  : sessionState() !== "running"
   559→                  ? `Cannot pause: session is ${sessionState()}`
   560→                  : "Pause the running session"
   561→              }
   562→            >
   563→              Pause
   564→            </button>
   565→            <button
   566→              type="button"
   567→              onClick={handleResume}
   568→              disabled={!canManage() || sessionState() !== "paused" || pendingAction() === "resume"}
   569→              title={
   570→                !canManage()
   571→                  ? "Bulk session controls are not permitted for your role."
   572→                  : pendingAction() === "resume"
   573→                  ? "Resume action is in progress..."
   574→                  : sessionState() !== "paused"
   575→                  ? `Cannot resume: session is ${sessionState()}`
   576→                  : "Resume the paused session"
   577→              }
   578→            >
   579→              Resume
   580→            </button>
   581→            <button
   582→              type="button"
   583→              class="danger"
   584→              onClick={handleStop}
   585→              disabled={!canManage() || pendingAction() === "stop"}
   586→              title={
   587→                !canManage()
   588→                  ? "Bulk session controls are not permitted for your role."
   589→                  : pendingAction() === "stop"
   590→                  ? "Kill action is in progress..."
   591→                  : "Kill the session"
   592→              }
   593→            >
   594→              Kill
   595→            </button>
   596→            <button
   597→              type="button"
   598→              class="neutral"
   599→               onClick={handleClose}
   600→              title="Close session viewer"
   601→              style={{ "margin-left": "auto" }}
   602→            >
   603→              ✕ Close
   604→            </button>
   605→          </div>
   606→        </div>
   607→      </header>
   608→      <Show when={actionNotice()}>
   609→        {(notice) => (
   610→          <p class={`notice-banner ${notice().tone}`} data-testid="session-action-notice">
   611→            {notice().message}
   612→          </p>
   613→        )}
   614→      </Show>
   615→      <Show when={session()?.error_message}>
   616→        {(errorMsg) => (
   617→          <p class="notice-banner error" data-testid="session-error-banner">
   618→            Session error: {errorMsg()}
   619→          </p>
   620→        )}
   621→      </Show>
   622→
   623→      <main class="session-layout">
   624→        <section class="session-panel">
   625→          <div class="panel-header">
   626→            <div>
   627→              <p class="panel-kicker">Live transcript</p>
   628→              <h2>Activity Feed</h2>
   629→            </div>
   630→            <div class="panel-tools">
   631→                <button
   632→                  type="button"
   633→                  class="neutral"
   634→                  onClick={handleLoadEarlier}
   635→                  disabled={!activityCursor() || activityHistoryLoading()}
   636→                  data-testid="session-load-earlier"
   637→                >
   638→                  {activityHistoryLoading() ? "Loading..." : "Load earlier"}
   639→                </button>
   640→              <input
   641→                type="search"
   642→                placeholder="Search transcript"
   643→                value={filter()}
   644→                onInput={(event) => setFilter(event.currentTarget.value)}
   645→              />
   646→              <button type="button" class="neutral" onClick={() => setAutoScroll(true)}>
   647→                Auto-scroll {autoScroll() ? "on" : "off"}
   648→              </button>
   649→            </div>
   650→          </div>
   651→
   652→          <div class="transcript" ref={transcriptRef} onScroll={handleScroll}>
   653→            <Show when={filteredMessages().length > 0} fallback={<p class="empty-state">No transcript yet.</p>}>
   654→              <For each={filteredMessages()}>
   655→                {(message) => (
   656→                  <article class={`transcript-item ${message.type}`}>
   657→                    <header>
   658→                      <span class="transcript-type">{message.type}</span>
   659→                      <Show when={message.open !== undefined}>
   660→                        <span class="transcript-status">{message.open ? "open" : "final"}</span>
   661→                      </Show>
   662→                      <time>{new Date(message.timestamp).toLocaleTimeString()}</time>
   663→                    </header>
   664→                    <div class="transcript-content">
   665→                      <For each={splitIntoBlocks(message.content)}>
   666→                        {(block) =>
   667→                          block.kind === "code" ? (
   668→                            <pre>
   669→                              <code data-language={block.lang}>{block.content}</code>
   670→                            </pre>
   671→                          ) : (
   672→                            <p>{block.content}</p>
   673→                          )
   674→                        }
   675→                      </For>
   676→                    </div>
   677→                  </article>
   678→                )}
   679→              </For>
   680→            </Show>
   681→          </div>
   682→        </section>
   683→
   684→        <section class="session-panel">
   685→          <div class="panel-header">
   686→            <div>
   687→              <p class="panel-kicker">Operational details</p>
   688→              <h2>Session Intel</h2>
   689→            </div>
   690→          </div>
   691→          <div class="session-metrics">
   692→            <div data-testid="session-info-id">
   693→              <span>ID</span>
   694→               <strong>{sessionId()}</strong>
   695→            </div>
   696→            <div data-testid="session-info-provider">
   697→              <span>Provider</span>
   698→              <strong>{providerType() || "unknown"}</strong>
   699→            </div>
   700→            <div data-testid="session-info-task">
   701→              <span>Current task</span>
   702→              <strong>{session()?.current_task || "None"}</strong>
   703→            </div>
   704→            <div data-testid="session-info-tokens-in">
   705→              <span>Tokens in</span>
   706→              <strong>{session()?.metrics?.tokens_in ?? "-"}</strong>
   707→            </div>
   708→            <div data-testid="session-info-tokens-out">
   709→              <span>Tokens out</span>
   710→              <strong>{session()?.metrics?.tokens_out ?? "-"}</strong>
   711→            </div>
   712→            <div data-testid="session-info-requests">
   713→              <span>Requests</span>
   714→              <strong>{session()?.metrics?.request_count ?? "-"}</strong>
   715→            </div>
   716→          </div>
   717→
   718→          <Show
   719→            when={providerType() === "pty"}
   720→            fallback={
   721→              <div class="empty-terminal">
   722→                <span>Terminal stream not available for this session.</span>
   723→              </div>
   724→            }
   725→          >
   726→            <TerminalView sessionId={sessionId()} title="PTY Stream" onStatusChange={setTerminalStatus} />
   727→          </Show>
   728→        </section>
   729→      </main>
   730→    </div>
   731→  )
   732→}
   733→
   734→function normalizeActivityMutation(data: ActivityEntryMutation | ActivityEntry | any): ActivityEntryMutation {
   735→  if (!data) return {}
   736→  if (Array.isArray(data.entries)) return { entries: data.entries }
   737→  if (data.entry) return data
   738→  if (data.id && data.kind) return { entry: data as ActivityEntry }
   739→  return {}
   740→}
   741→
   742→function toActivityMessageId(entry: ActivityEntry): string {
   743→  return `activity:${entry.id}`
   744→}
   745→
   746→function toActivityMessage(entry: ActivityEntry): TranscriptMessage {
   747→  return {
   748→    id: toActivityMessageId(entry),
   749→    entryId: entry.id,
   750→    revision: entry.rev,
   751→    open: entry.open,
   752→    kind: entry.kind,
   753→    type: mapActivityKindToType(entry.kind),
   754→    timestamp: entry.ts,
   755→    content: formatActivityContent(entry),
   756→  }
   757→}
   758→
   759→function mapActivityKindToType(kind: string): TranscriptMessage["type"] {
   760→  const normalized = kind.toLowerCase()
   761→  if (normalized.includes("error")) return "error"
   762→  if (normalized.includes("user")) return "user"
   763→  if (normalized.includes("agent") || normalized.includes("assistant")) return "agent"
   764→  return "system"
   765→}
   766→
   767→function formatActivityContent(entry: ActivityEntry): string {
   768→  const data = entry.data ?? {}
   769→  if (typeof data.text === "string" && data.text.trim().length > 0) return data.text
   770→  if (typeof data.content === "string" && data.content.trim().length > 0) return data.content
   771→  if (typeof data.message === "string" && data.message.trim().length > 0) return data.message
   772→  if (typeof data.summary === "string" && data.summary.trim().length > 0) return data.summary
   773→  if (typeof data.tool === "string") {
   774→    if (typeof data.result === "string") {
   775→      return `Tool ${data.tool}: ${data.result}`
   776→    }
   777→    return `Tool ${data.tool}`
   778→  }
   779→  return `${entry.kind}`
   780→}
   781→
   782→function getStreamStatusLabel(status: string): string {
   783→  const labels: Record<string, string> = {
   784→    connecting: "connecting...",
   785→    live: "live",
   786→    reconnecting: "reconnecting...",
   787→    disconnected: "disconnected",
   788→    connection_timeout: "timeout",
   789→    connection_failed: "failed",
   790→  }
   791→  return labels[status] || status
   792→}
   793→
   794→function getTerminalStatusLabel(status: string): string {
   795→  const labels: Record<string, string> = {
   796→    connecting: "connecting...",
   797→    live: "live",
   798→    resyncing: "resyncing",
   799→    closed: "closed",
   800→    error: "error",
   801→  }
   802→  return labels[status] || status
   803→}
   804→
   805→function formatActionError(error: unknown) {
   806→  if (error instanceof Error) {
   807→    const message = error.message || "Action failed."
   808→    if (message.toLowerCase().includes("csrf")) {
   809→      return "Action blocked by CSRF protection. Refresh to re-establish the token."
   810→    }
   811→    return message
   812→  }
   813→  return "Action failed."
   814→}
   815→
   816→function splitIntoBlocks(content: string) {
   817→  const blocks: { kind: "text" | "code"; content: string; lang?: string }[] = []
   818→  let lastIndex = 0
   819→  let match: RegExpExecArray | null = null
   820→
   821→  while ((match = CODE_BLOCK_REGEX.exec(content)) !== null) {
   822→    const [full, lang, code] = match
   823→    if (match.index > lastIndex) {
   824→      blocks.push({ kind: "text", content: content.slice(lastIndex, match.index) })
   825→    }
   826→    blocks.push({ kind: "code", content: code.trim(), lang: lang || "plain" })
   827→    lastIndex = match.index + full.length
   828→  }
   829→
   830→  if (lastIndex < content.length) {
   831→    blocks.push({ kind: "text", content: content.slice(lastIndex) })
   832→  }
   833→
   834→  return blocks
   835→}
   836→
   837→function downloadFile(filename: string, content: string) {
   838→  const blob = new Blob([content], { type: "text/plain" })
   839→  const url = URL.createObjectURL(blob)
   840→  const anchor = document.createElement("a")
   841→  anchor.href = url
   842→  anchor.download = filename
   843→  anchor.click()
   844→  URL.revokeObjectURL(url)
   845→}
   846→

